import { useContext } from "react";
import { ConfigStateContext } from "../../Contexts/Configurations/context";
import TagManager from "react-gtm-module";
import { useAuthCheck } from "../../Components/User/useAuthCheck";
import { useUser } from "../../Components/User/User.hook";

export const useGoogleTagManager = () => {
	const { google_tag_manager_id } = useContext(ConfigStateContext);
	const { isLoggedIn } = useAuthCheck();
	const { user } = useUser();
	const IS_BROWSER = typeof window !== "undefined";
	const userData = isLoggedIn ? { email: user.data?.email } : {};

	return {
		Init: () => {
			if (IS_BROWSER) TagManager.initialize({ ...userData, gtmId: google_tag_manager_id });
		},

		PageViewEvent: ({ page }: { page: string }) => {
			if (IS_BROWSER) {
				TagManager.initialize({
					gtmId: google_tag_manager_id,
					dataLayer: {
						...userData,
						page: page,
					},
					dataLayerName: "PageDataLayer",
				});
			}
		},

		Event: ({ name, data }: { name?: string; data?: object }) => {
			TagManager.initialize({
				gtmId: google_tag_manager_id,
				dataLayer: {
					sendUserData: userData,
					...data,
				},
				dataLayerName: name || "dataLayer",
			});
		},
	};
};
