import { Grid, Skeleton, Typography } from "antd";

import { useSEO } from "../hook";

const { Title } = Typography;
const { useBreakpoint } = Grid;

export const H1 = () => {
	const { seoMetaTags, error, loading } = useSEO();
	const screens = useBreakpoint();

	if (loading) return <Skeleton title paragraph={screens.xs ? true : false} active />;
	if (error || !seoMetaTags) return null;

	return <Title>{seoMetaTags.h1}</Title>;
};
