import { FC } from "react";
import { useRouter } from "next/router";
import { encodeHashUrl } from "../../Utils/Functions";

export interface LinkProps {
	pathname?: string;
	data?: any;
	as?: string;
	avoidRouter?: boolean
	callback?: () => void | null
}

export const PageLink: FC<LinkProps> = ({ pathname, data, as, children, avoidRouter, callback = null }) => {
	const router = useRouter();

	const onClick = (e) => {
		if(!avoidRouter){
			e.preventDefault();
			if(callback) {
				callback()
			}
			router.push(
				{
					pathname: pathname,
					query: {
						hashed: encodeHashUrl(data),
					},
				},
				"/" + as
			).then(() => {
				window.scrollTo(0, 0)
			});
		}
	};
	return (
		<>
			<a className="containerLink" onClick={onClick} href={"/" + as}>
				{children}
			</a>
			<style jsx>{`
				.containerLink {
					width: 100%;
					height: 100%;
				}
				.containerLink:hover {
					cursor: pointer;
				}
			`}</style>
		</>
	);
};
